import React from "react";
import { ListGroup, Card, Badge, Toast } from "react-bootstrap";
import { If, Then, Else } from "react-if";

function TodoList(props) {
  return (
    <ListGroup>
      {props.list.map((item) => (
        <Toast
          bg={"light"}
          key={item._id}
          text={"light" === "light" ? "dark" : "white"}
          style={{ width: "50%" , height: '5px'}}
          // className="rounded mr-2"
          onClose={() => props.handleDelete(item._id)}
        >
          <span>
            <Toast.Header   >
              <If condition={item.complete == false}>
                <Then>
                <Badge onClick={() => props.handleComplete(item._id)} pill variant="success">
                pending
              </Badge>{" "}
                </Then>
                <Else>
                <Badge onClick={() => props.handleComplete(item._id)}  pill variant="danger">
                complete
              </Badge>{" "}
                </Else>
              </If>

              {item.assignee}
            </Toast.Header>
            <Toast.Body>
              {item.text}
              <br />
              <small> Difficulty: {item.difficulty}</small>

            </Toast.Body>
          </span>
        </Toast>
      ))}
    </ListGroup>
  );
}

export default TodoList;
